import React, { Component, useState } from 'react';
import { View, Text, Image, TouchableHighlight, ActivityIndicator } from 'react-native';
import api from '../services/api.js';
import Nav from '../components/Nav';
import DoujinMenu from '../components/DoujinMenu';

import styles from './styles/styles';
import { ScrollView } from 'react-native-gesture-handler';

export function DoujinPage(){
    const [ page, setPage ] = useState(0);
    const [ totalPages, setTotalPages ] = useState(0);
    const [ images, setImages ] = useState([]);

    const next_page = (count) => {
        if(page + count <= totalPages){
            setPage(page + count);
        }else{
            setPage(totalPages - 1)
        }
    }

    const previous_page = (count) => {
        if(page - count >= 0){
            setPage(page - count);
        }else{
            setPage(0)
        }
    }



}

export default class Main extends Component {
  constructor(props){
    super(props)
    this.state = {
        data: undefined,
        index: 0,
        loading: false,
        primary_color: '#ED2553',
    }
  }
  
    ImageComponent = () => {
        return(
            <View style={styles.gallery_holder}>
                <this.MenuComponent />
                <TouchableHighlight onPress={() => { this.next_image(1) }}>
                    <Image style={styles.gallery_image} source={ { uri: this.state.data.images[this.state.index] } } alt=""/>
                </TouchableHighlight>
                <this.MenuComponent />
            </View>
        )
    }

    render(){
        return(
        <View style={[styles.main_background, styles.main]}>
                <Nav new_random={this.new_random.bind(this)} props={this.props}/>
                <ScrollView contentContainerStyle={styles.content}>
                    {this.state.loading ? <ActivityIndicator color={this.state.primary_color} size='large' /> : null}
                    {
                        this.state.data && !this.state.loading 
                        ? <this.ImageComponent />
                        : null
                    }
                </ScrollView>
        </View>
        );
    };
}
