import React, { useState, useEffect } from 'react';
import { Text } from 'react-native';
import api from '../../services/api.js';
import Nav from '../../components/Nav';
import MiniBox from '../../components/MiniBox';

import { MainContainer, ScrollableContainer, PaginationHolder, Button } from './styles';

export default function Main(){
  const [ thumbs, setThumbs ] = useState([]);
  const [ totalPages, setTotalPages ] = useState(0);
  const [ page, setPage ] = useState(1);

  useEffect(() => {
    api.get('/', { params: { page } }).then(res => {
      const data = res.data;
      setThumbs(data.doujins.splice(0, 24));
      setTotalPages(data.totalPages);
    }).catch(err => {
      console.log(err);
    })
  }, [ page ])

  const previous_page = () => {
    if(page > 1){
      setPage(page - 1)
    }
  }

  const next_page = () => {
    if(page < totalPages){
      setPage(page + 1)
    }
  }

  return(
    <MainContainer>
        {/* <Nav /> */}
        <ScrollableContainer contentContainerStyle={{ 'justifyContent': 'center', 'alignItems': 'center' }}>
          {
              thumbs.length > 0
              ? thumbs.map(item => (<MiniBox id={item.id} images={item.cover} lang={'english'} title={item.title} key={`doujin_${item.id}`} />))
              : null
          }
        </ScrollableContainer>
        <PaginationHolder>
            <Button onPress={previous_page} title='Anterior' />
            <Text>{page} de {totalPages}</Text>
            <Button onPress={next_page} title='PrÃ³ximo' />
        </PaginationHolder>
    </MainContainer>
  );

}
